package leetcode.contest.week313;

import java.util.HashMap;
import java.util.HashSet;
import java.util.Map;
import java.util.Set;

public class Test6195_对字母串可执行的最大删除数 {

    public static void main(String[] args) {
        System.out.println(new Solution().deleteString("abcabcdabc"));
        System.out.println(new Solution().deleteString("aaabaab"));
        System.out.println(new Solution().deleteString("aaaaa"));
        System.out.println(new Solution().deleteString(""));
    }

    static class Solution {
        Map<String, Integer> visitedMap = new HashMap<>();
        int max = 0;

        public int deleteString(String s) {
            Set<Character> set = new HashSet<>();
            for (char c : s.toCharArray()) {
                set.add(c);
            }
            if (set.size() == 1) {
                return s.length();
            }
            dfs(s, 0);
            return max;
        }

        private void dfs(String s, int cnt) {
            max = Math.max(max, cnt);
            if (s.isEmpty()) {
                return;
            }
            if (visitedMap.containsKey(s) && visitedMap.get(s) >= cnt) {
                return;
            } else {
                visitedMap.put(s, cnt);
            }
            // 尝试找前缀并递归
            for (int i = 1; i <= s.length() / 2; i++) {
                String prefix = s.substring(0, i);
                if (s.substring(i).startsWith(prefix)) {
                    dfs(s.substring(i), cnt + 1);
                }
            }
            // 删除全部
            dfs("", cnt + 1);
        }
    }

}
